mapper.select:
select user_id userId, user_pwd userPwd, user_mail userMail,
user_birth userBirth, user_nm userNm, user_nic userNic
 from users where user_id=#{userId}

mapper.update:
update users
set 
user_nic =#{userNic}
,user_nm =#{userNm}
,user_birth =#{userBirth}
,user_mail =#{userMail}
,user_pwd =#{userPwd}
where user_id =#{userId}
